import React, { useState } from 'react';
import { View, Text, TouchableOpacity, Image, StyleSheet } from 'react-native';
import FontAwesome5 from 'react-native-vector-icons/FontAwesome5';
import ImagePicker from 'react-native-image-crop-picker';

const UpdateProfilePicture = ({ navigation }) => {
  const [image, setImage] = useState(null);

  const handleChoosePhoto = async () => {
    try {
      const pickedImage = await ImagePicker.openPicker({
        width: 300,
        height: 300,
        cropping: true,
      });

      setImage(pickedImage.path);
    } catch (error) {
      console.log(error);
    }
  };

  const handleUpdate = () => {
    // Handle the logic to update the profile picture in App.js
    // You can pass the selected image URI back to App.js and update the state there
    navigation.navigate('Home', { newProfilePicture: image });
  };

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Update Profile Picture</Text>
      </View>

      <View style={styles.content}>
        <TouchableOpacity
          style={styles.profilePictureButton}
          onPress={handleChoosePhoto}
        >
          <View style={styles.pencilIconContainer}>
            <FontAwesome5 name='camera' style={styles.pencilIcon} />
          </View>
          <View style={styles.profilePictureContainer}>
            {image ? (
              <Image source={{ uri: image }} style={styles.profilePicture} />
            ) : (
              <Image
                source={require('../assets/default-avatar.png')}
                style={styles.profilePicture}
              />
            )}
          </View>
        </TouchableOpacity>

        <TouchableOpacity style={styles.updateButton} onPress={handleUpdate}>
          <Text style={styles.updateButtonText}>Update</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    padding: 15,
    alignItems: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#4CA5FD',
  },
  content: {
    alignItems: 'center',
  },
  profilePictureButton: {
    marginBottom: 20,
    color: 'black',
    position: 'relative',
  },
  profilePictureContainer: {
    position: 'relative',
    borderRadius: 55,
    overflow: 'hidden',
    borderColor: '#4CA5FD',
    borderWidth: 3,
  },
  profilePicture: {
    width: 110,
    height: 110,
    borderColor: 'white',
    borderWidth: 2,
  },
  pencilIconContainer: {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: [{ translateX: 35 }, { translateY: -50 }],
    borderRadius: 15,
    zIndex: 1,
    padding: 5,
    backgroundColor: 'white',
  },
  pencilIcon: {
    color: '#4CA5FD',
    fontSize: 17,
  },
  updateButton: {
    marginTop: 20,
    backgroundColor: '#4CA5FD',
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 8,
  },
  updateButtonText: {
    color: 'white',
    fontSize: 16,
  },
});

export default UpdateProfilePicture;
