import React from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Image } from 'react-native';
import FontAwesome5 from 'react-native-vector-icons/FontAwesome5';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';

import UpdateProfilePicture from './UpdateProfilePicture';

const HomeScreen = ({ navigation }) => {
  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Edit Profile</Text>
      </View>

      <View style={styles.profileSection}>
        <TouchableOpacity
          style={styles.profilePictureButton}
          onPress={() => navigation.navigate('UpdateProfilePicture')}
        >
          <View style={styles.pencilIconContainer}>
            <FontAwesome5 name='pencil-alt' style={styles.pencilIcon} />
          </View>
          <View style={styles.profilePictureContainer}>
            {/* Use your actual profile picture source */}
            <Image
              source={require('../assets/default-avatar.png')}
              style={styles.profilePicture}
            />
          </View>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const Stack = createStackNavigator();

const App = () => {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName='Home'>
        <Stack.Screen
          name='Home'
          component={HomeScreen}
          options={{
            headerShown: true,
            headerTitle: '',
            headerStyle: { borderBottomWidth: 0 },
          }}
        />
        <Stack.Screen
          name='UpdateProfilePicture'
          component={UpdateProfilePicture}
          options={{
            headerShown: true,
            headerTitle: 'Update Profile Picture',
            headerStyle: { borderBottomWidth: 0 },
          }}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
};

const styles = StyleSheet.create({
  // ... your existing styles

  updateButton: {
    backgroundColor: 'black',
    padding: 10,
    borderRadius: 8,
    marginTop: 16,
    shadowColor: '#000000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.4,
    shadowRadius: 4,
    elevation: 5,
  },
});

export default App;
