import React, { useState } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Image,
  Alert,
} from 'react-native';

const UpdateProfilePicture = ({ navigation }) => {
  const [file, setFile] = useState(null);

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];

    if (selectedFile) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFile(reader.result);
      };
      reader.readAsDataURL(selectedFile);
    }
  };

  const pickImage = () => {
    this.fileInput.click();
  };

  const handleUpdate = () => {
    // Add logic to update the profile picture in App.js state
    // You may want to use a state management library like Redux or React Context for this purpose
    // For now, let's just navigate back to the HomeScreen
    navigation.goBack();
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Add Image:</Text>

      {/* Custom file input button with image preview */}
      <TouchableOpacity style={styles.fileInputContainer} onPress={pickImage}>
        <View style={styles.imageContainer}>
          {file ? (
            <Image source={{ uri: file }} style={styles.image} />
          ) : (
            <Text style={styles.fileInputLabel}>Choose Image</Text>
          )}
        </View>
      </TouchableOpacity>

      {/* Hidden file input */}
      <input
        type='file'
        accept='image/*'
        style={{ display: 'none' }}
        onChange={handleFileChange}
        ref={(fileInput) => (this.fileInput = fileInput)}
      />

      {/* Button to update profile picture */}
      {file && (
        <TouchableOpacity style={styles.updateButton} onPress={handleUpdate}>
          <Text style={styles.buttonText}>Update</Text>
        </TouchableOpacity>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 16,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  input: {
    height: 200,
    padding: 15,
    backgroundColor: '#FFF',
    borderRadius: 10,
    borderColor: '#C0C0C0',
    borderWidth: 1,
    marginBottom: 20,
    width: '100%',
  },
  updateButton: {
    backgroundColor: 'black',
    paddingVertical: 10,
    paddingHorizontal: 20,
  },
  buttonText: {
    color: 'white',
    fontSize: 20,
  },
});

export default UpdateAbout;
