import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
} from 'react-native';

const UpdateAbout = ({ navigation, route }) => {
  const [phoneNumber, setPhoneNumber] = useState('');

  const handleSavePhoneNumber = () => {
    const { handleEditPhoneNumber } = route.params;
    const parsedPhoneNumber = parseInt(phoneNumber, 10);

    if (!isNaN(parsedPhoneNumber)) {
      handleEditPhoneNumber(parsedPhoneNumber);
      navigation.goBack();
    } else {
      // Handle invalid input (non-integer)
      alert('Please enter a valid phone number.');
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>What's your phone number?</Text>

      <TextInput
        style={styles.input}
        placeholder='Your phone number'
        keyboardType='numeric'
        value={phoneNumber}
        onChangeText={(text) => setPhoneNumber(text)}
      />

      <TouchableOpacity
        style={styles.updateButton}
        onPress={handleSavePhoneNumber}
      >
        <Text style={styles.buttonText}>Update</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'flex-start',
    backgroundColor: 'white',
    paddingTop: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  input: {
    paddingVertical: 15,
    paddingHorizontal: 15,
    backgroundColor: '#FFF',
    borderRadius: 10,
    borderColor: '#C0C0C0',
    borderWidth: 1,
    marginBottom: 20,
    width: 250,
  },
  updateButton: {
    backgroundColor: 'black',
    paddingVertical: 10,
    paddingHorizontal: 20,
  },
  buttonText: {
    color: 'white',
    fontSize: 20,
  },
});

export default UpdateAbout;
