import React, { useState } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Image,
  Button,
  Platform,
  Alert,
} from 'react-native';
import { useNavigation } from '@react-navigation/native';
import * as ImagePicker from 'expo-image-picker';

const SERVER_URL = 'http://localhost:3000';

const createFormData = (photo, body = {}) => {
  const data = new FormData();

  data.append('photo', {
    name: photo.fileName,
    type: photo.type,
    uri: Platform.OS === 'ios' ? photo.uri.replace('file://', '') : photo.uri,
  });

  Object.keys(body).forEach((key) => {
    data.append(key, body[key]);
  });

  return data;
};

const UpdateProfilePicture = () => {
  const [profilePicture, setProfilePicture] = useState(null);
  const navigation = useNavigation();

  const pickImage = async () => {
    const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();

    if (status !== 'granted') {
      Alert.alert(
        'Permission Denied',
        'Sorry, we need camera roll permission to upload images.'
      );
    } else {
      const result = await ImagePicker.launchImageLibraryAsync();

      if (!result.cancelled) {
        setProfilePicture(result);
      }
    }
  };

  const handleUploadProfilePicture = () => {
    if (profilePicture) {
      fetch(`${SERVER_URL}/api/upload`, {
        method: 'POST',
        body: createFormData(profilePicture, { userId: '123' }),
      })
        .then((response) => response.json())
        .then((response) => {
          console.log('Profile picture uploaded:', response);
          navigation.goBack(); // Navigate back to the HomeScreen or any other screen
        })
        .catch((error) => {
          console.error('Error uploading profile picture:', error);
        });
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Update Profile Picture</Text>

      {/* Button to choose an image */}
      <TouchableOpacity style={styles.selectImageButton} onPress={pickImage}>
        <Text style={styles.buttonText}>Select Image</Text>
      </TouchableOpacity>

      {/* Conditionally render the image or error message */}
      {profilePicture ? (
        // Display the selected image
        <View style={styles.imageContainer}>
          <Image
            source={{ uri: profilePicture.uri }}
            style={styles.profilePicture}
          />
          <Button
            title='Upload Profile Picture'
            onPress={handleUploadProfilePicture}
          />
        </View>
      ) : (
        // Display an error message if there's an error or no image selected
        <Text style={styles.errorText}>No image selected</Text>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'white',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  selectImageButton: {
    backgroundColor: 'darkturquoise',
    paddingVertical: 10,
    paddingHorizontal: 20,
    marginBottom: 20,
  },
  buttonText: {
    color: 'white',
    fontSize: 20,
  },
  imageContainer: {
    borderRadius: 8,
    marginBottom: 20,
    shadowColor: '#000000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.4,
    shadowRadius: 4,
    elevation: 5,
  },
  profilePicture: {
    width: 200,
    height: 200,
    borderRadius: 8,
    marginBottom: 20,
  },
  errorText: {
    color: 'red',
    marginTop: 16,
  },
});

export default UpdateProfilePicture;
