import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Image } from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import { Alert } from 'react-native';

const UpdateProfilePicture = () => {
  // Stores the selected image URI
  const [file, setFile] = useState(null);

  // Function to pick an image from
  // the device's media library
  const pickImage = async () => {
    // Show a popup when the button is pressed
    Alert.alert(
      'Choose Image',
      'Select an image from your library',
      [
        {
          text: 'Cancel',
          onPress: () => console.log('Image picking cancelled'),
          style: 'cancel',
        },
        { text: 'OK', onPress: handleImageSelection },
      ],
      { cancelable: false }
    );
  };

  // Function to handle image selection
  const handleImageSelection = async () => {
    const result = await ImagePicker.launchImageLibraryAsync();

    console.log('ImagePicker result:', result);

    if (result.cancelled) {
      console.log('Image picking cancelled');
    } else {
      setFile(result.uri);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Add Image:</Text>

      {/* Button to choose an image */}
      <TouchableOpacity style={styles.button} onPress={pickImage}>
        <Text style={styles.buttonText}>Choose Image</Text>
      </TouchableOpacity>

      {/* Conditionally render the image or error message */}
      {file ? (
        // Display the selected image
        <View style={styles.imageContainer}>
          <Image source={{ uri: file }} style={styles.image} />
        </View>
      ) : (
        // Display an error message if there's an error or no image selected
        <Text style={styles.errorText}>No image selected</Text>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 16,
  },
  header: {
    fontSize: 20,
    marginBottom: 16,
  },
  button: {
    backgroundColor: '#007AFF',
    padding: 10,
    borderRadius: 8,
    marginBottom: 16,
    shadowColor: '#000000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.4,
    shadowRadius: 4,
    elevation: 5,
  },
  buttonText: {
    color: '#FFFFFF',
    fontSize: 16,
    fontWeight: 'bold',
  },
  imageContainer: {
    borderRadius: 8,
    marginBottom: 16,
    shadowColor: '#000000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.4,
    shadowRadius: 4,
    elevation: 5,
  },
  image: {
    width: 200,
    height: 200,
    borderRadius: 8,
  },
  errorText: {
    color: 'red',
    marginTop: 16,
  },
});

export default UpdateProfilePicture;
