import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Image } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import UpdateName from './UpdateName';
import UpdateProfilePicture from './UpdateProfilePicture'; // Add the new screen

const HomeScreen = ({ navigation }) => {
  const [name, setName] = useState('Micah Smith');

  const handleEditName = (newName) => {
    setName(newName);
  };

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Edit Profile</Text>
      </View>

      <View style={styles.profileSection}>
        <TouchableOpacity
          style={styles.profilePictureButton}
          onPress={() => navigation.navigate('UpdateProfilePicture')}
        >
          {/* Assuming you have an image URL for the profile picture */}
          <Image
            source={{
              uri: 'https://api-private.atlassian.com/users/9873c2c90e992a23c62f1238b2996b87/avatar',
            }}
            style={styles.profilePicture}
          />
        </TouchableOpacity>

        <Text style={styles.profileTitleText}>Name</Text>
        <TouchableOpacity
          style={styles.editButton}
          onPress={() => navigation.navigate('UpdateName', { handleEditName })}
        >
          <View style={styles.itemContainer}>
            <View style={styles.itemLeft}>
              <Text style={styles.itemText}>{name}</Text>
            </View>
            <Text style={styles.itemRight}>{' ›'}</Text>
          </View>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const Stack = createStackNavigator();

const App = () => {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName='Home'>
        <Stack.Screen
          name='Home'
          component={HomeScreen}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name='UpdateName'
          component={UpdateName}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name='UpdateProfilePicture'
          component={UpdateProfilePicture}
          options={{ headerShown: false }}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',
    alignItems: 'center', // Center items horizontally
    justifyContent: 'center', // Center items vertically
  },
  header: {
    padding: 15,
    alignItems: 'center',
  },
  title: {
    fontSize: 30,
    fontWeight: 'bold',
    color: 'darkturquoise',
  },
  profileSection: {
    padding: 25,
    margin: 20,
    alignItems: 'center',
  },
  profilePictureButton: {
    marginBottom: 20,
  },
  profilePicture: {
    width: 100,
    height: 100,
    borderRadius: 50,
  },
  profileTitleText: {
    fontSize: 15,
    alignContent: 'left',
    fontWeight: 'bold',
    color: '#C8CEE3',
  },
  editButton: {
    backgroundColor: 'white',
    alignSelf: 'flex-start',
    width: 600,
    height: 70,
    borderBottomColor: '#C8CEE3',
    borderBottomWidth: 2,
  },
  itemContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    width: '100%', // Ensure the width is 100%
  },
  itemText: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  itemLeft: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  itemRight: {
    fontSize: 60,
    color: '#C8CEE3',
  },
});

export default App;
