import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Image } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import UpdateName from './UpdateName';
import UpdateProfilePicture from './UpdateProfilePicture';
import FontAwesome5 from 'react-native-vector-icons/FontAwesome5';

const HomeScreen = ({ navigation }) => {
  const [name, setName] = useState('Micah Smith');

  const handleEditName = (newName) => {
    setName(newName);
  };

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Edit Profile</Text>
      </View>

      <View style={styles.profileSection}>
        <TouchableOpacity
          style={styles.profilePictureButton}
          onPress={() => navigation.navigate('UpdateProfilePicture')}
        >
          <View style={styles.pencilIconContainer}>
            <FontAwesome5 name='pencil-alt' style={styles.pencilIcon} />
          </View>
          <View style={styles.profilePictureContainer}>
            <Image
              source={require('../assets/default-avatar.png')}
              style={styles.profilePicture}
            />
          </View>
        </TouchableOpacity>

        <View style={styles.nameAndButtonContainer}>
          <Text style={styles.profileTitleText}>Name</Text>
          <TouchableOpacity
            style={styles.editButton}
            onPress={() =>
              navigation.navigate('UpdateName', { handleEditName })
            }
          >
            <View style={styles.itemContainer}>
              <View style={styles.itemLeft}>
                <Text style={styles.itemText}>{name}</Text>
              </View>
              <Text style={styles.itemRight}>{' ›'}</Text>
            </View>
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
};

const Stack = createStackNavigator();

const App = () => {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName='Home'>
        <Stack.Screen
          name='Home'
          component={HomeScreen}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name='UpdateName'
          component={UpdateName}
          options={{ headerShown: false }}
        />
        <Stack.Screen
          name='UpdateProfilePicture'
          component={UpdateProfilePicture}
          options={{ headerShown: false }}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',
    alignItems: 'center',
    justifyContent: 'center',
  },
  header: {
    padding: 15,
    alignItems: 'center',
  },
  title: {
    fontSize: 30,
    fontWeight: 'bold',
    color: '#4CA5FD',
  },
  profileSection: {
    padding: 25,
    margin: 20,
    alignItems: 'center',
  },
  profilePictureButton: {
    marginBottom: 20,
    position: 'relative',
  },
  profilePictureContainer: {
    position: 'absolute',
    borderRadius: 55,
    overflow: 'hidden',
    borderColor: '#4CA5FD',
    borderWidth: 3,
  },
  profilePicture: {
    width: 110,
    height: 110,
  },
  pencilIconContainer: {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: [{ translateX: 35 }, { translateY: -50 }],
    borderRadius: 15,
    zIndex: 1,
    padding: 5,
    backgroundColor: 'white',
  },
  pencilIcon: {
    color: '#4CA5FD',
  },
  nameAndButtonContainer: {
    alignItems: 'flex-start',
    width: '100%',
  },
  profileTitleText: {
    fontSize: 15,
    fontWeight: 'bold',
    color: '#C8CEE3',
  },
  editButton: {
    backgroundColor: 'white',
    alignSelf: 'flex-start',
    width: 600,
    height: 70,
    borderBottomColor: '#C8CEE3',
    borderBottomWidth: 2,
  },
  itemContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    width: '100%',
  },
  itemText: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  itemLeft: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  itemRight: {
    fontSize: 60,
    color: '#C8CEE3',
  },
});

export default App;
