import React, { useState, useEffect } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Image } from 'react-native';
import * as ImagePicker from 'expo-image-picker';

const UpdateProfilePicture = ({ route, navigation }) => {
  const [file, setFile] = useState(null);

  useEffect(() => {
    if (route.params?.newProfilePicture) {
      setFile(route.params.newProfilePicture);
    }
  }, [route.params?.newProfilePicture]);

  const pickImage = async () => {
    const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();

    if (status !== 'granted') {
      alert('Sorry, we need camera roll permissions to make this work!');
      return;
    }

    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [1, 1],
      quality: 1,
    });

    if (!result.cancelled) {
      setFile(result.uri);
    }
  };

  const handleUpdate = () => {
    // Pass the updated profile picture URI back to App.js when the "Update" button is pressed
    route.params.handleProfilePictureUpdate(file);
    // Navigate back to App.js
    navigation.goBack();
  };
  const handleRedirect = () => {
    // Check if a file is selected before redirecting
    if (file) {
      // Navigate to App.js with the new image URI
      navigation.navigate('App', { newImageURI: file });
    } else {
      // Show an alert or perform any other action if no image is selected
      alert('Please choose an image before redirecting.');
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Add Image:</Text>

      {/* Button to choose an image */}
      <TouchableOpacity style={styles.fileInputContainer} onPress={pickImage}>
        <View style={styles.imageContainer}>
          {file ? (
            <Image source={{ uri: file }} style={styles.image} />
          ) : (
            <Text style={styles.fileInputLabel}>Choose Image</Text>
          )}
        </View>
      </TouchableOpacity>

      {/* Update button */}
      {file && (
        <TouchableOpacity style={styles.updateButton} onPress={handleUpdate}>
          <Text style={styles.buttonText}>Update</Text>
        </TouchableOpacity>
      )}

      {/* New button for redirecting with the new image */}
      <TouchableOpacity style={styles.redirectButton} onPress={handleRedirect}>
        <Text style={styles.buttonText}>Redirect</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 16,
  },
  header: {
    fontSize: 20,
    marginBottom: 16,
  },
  fileInputContainer: {
    marginBottom: 16,
  },
  imageContainer: {
    width: 200,
    height: 200,
    borderRadius: 8,
    backgroundColor: '#e0e0e0',
    justifyContent: 'center',
    alignItems: 'center',
  },
  image: {
    width: '100%',
    height: '100%',
    borderRadius: 8,
  },
  fileInputLabel: {
    fontSize: 16,
    color: '#007AFF',
    fontWeight: 'bold',
  },
  updateButton: {
    backgroundColor: 'black',
    padding: 10,
    borderRadius: 8,
    marginTop: 16,
    shadowColor: '#000000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.4,
    shadowRadius: 4,
    elevation: 5,
  },
  buttonText: {
    color: '#FFFFFF',
    fontSize: 16,
    fontWeight: 'bold',
  },
  redirectButton: {
    backgroundColor: '#007AFF', // Change color as needed
    padding: 10,
    borderRadius: 8,
    marginTop: 16,
    shadowColor: '#000000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.4,
    shadowRadius: 4,
    elevation: 5,
  },
});
export default UpdateProfilePicture;
