import React, { useState, useEffect } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Image } from 'react-native';
import * as ImagePicker from 'expo-image-picker';

const UpdateProfilePicture = ({ route }) => {
  const [file, setFile] = useState(null);

  useEffect(() => {
    if (route.params?.newProfilePicture) {
      setFile(route.params.newProfilePicture);
    }
  }, [route.params?.newProfilePicture]);

  const pickImage = async () => {
    const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();

    if (status !== 'granted') {
      alert('Sorry, we need camera roll permissions to make this work!');
      return;
    }

    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [1, 1],
      quality: 1,
    });

    if (!result.cancelled) {
      setFile(result.uri);
      // Pass the updated profile picture URI back to App.js
      route.params.handleProfilePictureUpdate(result.uri);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Add Image:</Text>

      {/* Button to choose an image */}
      <TouchableOpacity style={styles.fileInputContainer} onPress={pickImage}>
        <View style={styles.imageContainer}>
          {file ? (
            <Image source={{ uri: file }} style={styles.image} />
          ) : (
            <Text style={styles.fileInputLabel}>Choose Image</Text>
          )}
        </View>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 16,
  },
  header: {
    fontSize: 20,
    marginBottom: 16,
  },
  fileInputContainer: {
    marginBottom: 16,
  },
  imageContainer: {
    width: 200,
    height: 200,
    borderRadius: 8,
    backgroundColor: '#e0e0e0',
    justifyContent: 'center',
    alignItems: 'center',
  },
  image: {
    width: '100%',
    height: '100%',
    borderRadius: 8,
  },
  fileInputLabel: {
    fontSize: 16,
    color: '#007AFF',
    fontWeight: 'bold',
  },
});

export default UpdateProfilePicture;
